import React from "react";
import { Box, Grid, Card, CardContent, Typography, Chip } from "@mui/material";

const announcements = [
  {
    title: "Sunday Service Times",
    content: "Join us every Sunday at 9:00am and 11:30am for worship and fellowship.",
    tag: "Worship",
  },
  {
    title: "Youth Bible Study",
    content: "Fridays at 6:00pm in the Youth Hall. All teens welcome!",
    tag: "Youth",
  },
  {
    title: "Community Outreach",
    content: "Sign up to volunteer for our monthly outreach program. Let's make a difference together!",
    tag: "Outreach",
  },
  {
    title: "Prayer Meeting",
    content: "Wednesday evenings at 7:00pm in the main sanctuary. All are invited.",
    tag: "Prayer",
  },
  {
    title: "Children's Choir Auditions",
    content: "Auditions for ages 6-12 this Saturday at 10:00am in Room 3.",
    tag: "Music",
  },
];

const Announcements = () => (
  <Box sx={{ p: { xs: 2, md: 4 }, background: "#f7f8fa", minHeight: "100vh" }}>
    <Typography variant="h4" fontWeight={700} mb={3} color="#333" fontFamily="Inter, Roboto, sans-serif">
      Announcements
    </Typography>
    <Grid container spacing={3}>
      {announcements.map((a, idx) => (
        <Grid item xs={12} sm={6} md={4} key={idx}>
          <Card
            elevation={2}
            sx={{
              borderRadius: 3,
              minHeight: 170,
              background: "#fff",
              boxShadow: "0 2px 12px 0 rgba(90, 100, 120, 0.07)",
              transition: "box-shadow 0.2s",
              '&:hover': {
                boxShadow: "0 6px 24px 0 rgba(90, 100, 120, 0.13)",
              },
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
            }}
          >
            <CardContent>
              <Box display="flex" alignItems="center" mb={1}>
                <Chip label={a.tag} size="small" sx={{ mr: 1, background: "#6A5ACD", color: "#fff", fontWeight: 500, fontSize: 12 }} />
                <Typography variant="h6" fontWeight={600} color="#222" fontFamily="Inter, Roboto, sans-serif">
                  {a.title}
                </Typography>
              </Box>
              <Typography variant="body1" color="#555" fontSize={16} fontFamily="Inter, Roboto, sans-serif">
                {a.content}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  </Box>
);

export default Announcements;
