import React, { useEffect, useRef, useState } from "react";
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
  Stack,
} from "@mui/material";

const ChatBox = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "How can I assist you today?" },
  ]);
  const [input, setInput] = useState("");
  const chatRef = useRef(null);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { sender: "user", text: input }]);
    setInput("");
  };

  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        height: "00vh",
        background: "transparent",
        borderRadius: 3,
        px: 2,
      }}
    >
      <Box
        sx={{
          flexGrow: 1,
          overflowY: "auto",
          py: 2,
          px: 1,
        }}
      >
        <Stack spacing={2}>
          {messages.map((msg, idx) => (
            <Box
              key={idx}
              alignSelf={msg.sender === "user" ? "flex-end" : "flex-start"}
              maxWidth="80%"
            >
              <Paper
                elevation={0}
                sx={{
                  px: 2,
                  py: 1,
                  borderRadius: 3,
                  backgroundColor:
                    msg.sender === "user" ? "#e3f2fd" : "#f5f5f5",
                }}
              >
                <Typography
                  variant="body2"
                  fontSize="0.95rem"
                  color="text.primary"
                >
                  {msg.text}
                </Typography>
              </Paper>
            </Box>
          ))}
          <div ref={chatRef} />
        </Stack>
      </Box>

      <Box
        sx={{
          display: "flex",
          gap: 1,
          alignItems: "center",
          mt: "auto",
          pb: 1,
          pt: 1,
          position: "sticky",
          bottom: 0,
          backgroundColor: "#fff",
        }}
      >
        <TextField
          id="assistant-input"
          placeholder="Ask a question..."
          variant="outlined"
          fullWidth
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
          sx={{
            borderRadius: "30px",
            backgroundColor: "#f9f9f9",
            "& .MuiOutlinedInput-root": {
              borderRadius: "30px",
            },
          }}
        />
        <Button
          onClick={handleSend}
          variant="contained"
          sx={{ borderRadius: "30px", px: 3 }}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
};

export default ChatBox;
