import React, { useState } from "react";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Box from "@mui/material/Box";
import ShareIcon from "@mui/icons-material/Share";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import Brightness7Icon from "@mui/icons-material/Brightness7";
import logo from "../assets/logo.png";

const Header = () => {
  const [darkMode, setDarkMode] = useState(false);

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: "Nairobi Chapel – Ngong Road",
        url: window.location.href,
      });
    } else {
      // fallback: copy to clipboard
      navigator.clipboard.writeText(window.location.href);
      alert("Link copied to clipboard!");
    }
  };

  const handleToggleTheme = () => {
    setDarkMode((prev) => !prev);
    // Theme switching logic would go here
  };

  return (
    <AppBar
      position="fixed"
      elevation={0}
      sx={{
        zIndex: (theme) => theme.zIndex.drawer + 1,
        background: "0a0a0a", // calm semi-dark, slightly translucent
        backdropFilter: "blur(8px)",
        boxShadow: "none",
      }}
    >
      <Toolbar sx={{ justifyContent: "space-between" }}>
        <Box display="flex" alignItems="center">
          <img src={logo} alt="Nairobi Chapel Logo" style={{ height: 40, marginRight: 12 }} />
        </Box>
        <Box display="flex" alignItems="center">
          <IconButton color="inherit" onClick={handleShare} sx={{ mr: 1 }}>
            <ShareIcon />
          </IconButton>
          <IconButton color="inherit" onClick={handleToggleTheme}>
            {darkMode ? <Brightness7Icon /> : <Brightness4Icon />}
          </IconButton>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
