import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import IconButton from "@mui/material/IconButton";
import Tooltip from "@mui/material/Tooltip";
import Box from "@mui/material/Box";

import HomeIcon from "@mui/icons-material/Home";
import CampaignIcon from "@mui/icons-material/Campaign";
import GroupsIcon from "@mui/icons-material/Groups";
import PlayCircleIcon from "@mui/icons-material/PlayCircle";
import EventIcon from "@mui/icons-material/Event";
import RoomIcon from "@mui/icons-material/Room";
import ConnectWithoutContactIcon from "@mui/icons-material/ConnectWithoutContact";
import MenuOpenIcon from "@mui/icons-material/MenuOpen";
import MenuIcon from "@mui/icons-material/Menu";

const navItems = [
  { text: "Home", icon: <HomeIcon /> },
  { text: "Notices", icon: <CampaignIcon /> },
  { text: "Ministries", icon: <GroupsIcon /> },
  { text: "Sermons", icon: <PlayCircleIcon /> },
  { text: "Events", icon: <EventIcon /> },
  { text: "Location", icon: <RoomIcon /> },
  { text: "Connect", icon: <ConnectWithoutContactIcon /> },
];

const Sidebar = () => {
  const [open, setOpen] = useState(true);
  const drawerWidth = open ? 220 : 64;
  const location = useLocation();
  const highlightColor = "#6A5ACD";

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        whiteSpace: "nowrap",
        transition: "width 0.3s",
        [`& .MuiDrawer-paper`]: {
          width: drawerWidth,
          boxSizing: "border-box",
          background: "transparent",
          color: "#555555",
          borderRight: "none",
          transition: "width 0.3s",
          height: '100vh',
          position: 'fixed',
          top: 0,
          left: 0,
          overflowY: 'auto',
          overflowX: 'hidden',
        },
      }}
    >
      {/* Toggle Button Floating on Right Edge */}
      <IconButton
        onClick={() => setOpen((prev) => !prev)}
        color="primary"
        size="small"
        aria-label="Toggle sidebar"
        sx={{
          position: "absolute",
          right: -16,
          top: "50%",
          transform: "translateY(-50%)",
          zIndex: 1301,
          backgroundColor: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
          borderRadius: "50%",
          transition: "all 0.2s ease-in-out",
          "&:hover": {
            backgroundColor: "#f0f0f0",
          },
        }}
      >
        {open ? <MenuOpenIcon /> : <MenuIcon />}
      </IconButton>

      {/* Nav Items */}
      <List sx={{ mt: 6 }}>
        {navItems.map((item, idx) => {
          const route = item.text.toLowerCase() === "home" ? "/" : `/${item.text.toLowerCase()}`;
          const isActive = window.location.pathname === route;
          return (
            <Tooltip title={!open ? item.text : ""} placement="right" key={item.text} arrow disableHoverListener={open}>
              <ListItem
                button
                key={item.text}
                component={Link}
                to={route}
                sx={{
                  minHeight: 48,
                  justifyContent: open ? "initial" : "center",
                  px: 2.5,
                  borderRadius: 2,
                  my: 0.5,
                  mt: idx === 0 ? 2 : 0, // Add margin-top to the first item
                  fontFamily: 'Inter, Roboto, Arial, sans-serif',
                  color: "#555555",
                  background: isActive ? "rgba(85,85,85,0.08)" : "transparent",
                  fontWeight: isActive ? 600 : 400,
                  boxShadow: isActive ? "0 2px 8px 0 rgba(85,85,85,0.04)" : "none",
                  transition: "background 0.2s, color 0.2s, box-shadow 0.2s",
                  "&:hover": {
                    background: "rgba(85,85,85,0.12)",
                    color: "#222831",
                  },
                }}
              >
                <ListItemIcon
                  sx={{
                    minWidth: 0,
                    mr: open ? 2 : "auto",
                    justifyContent: "center",
                    color: isActive ? highlightColor : "#777",
                    transition: "color 0.2s ease",
                  }}
                >
                  {item.icon}
                </ListItemIcon>

                {open && (
                  <ListItemText
                    primary={item.text}
                    primaryTypographyProps={{
                      fontSize: 15,
                      fontWeight: isActive ? 600 : 500,
                      letterSpacing: 0.2,
                      color: isActive ? highlightColor : "#444",
                    }}
                  />
                )}
              </ListItem>
            </Tooltip>
          );
        })}
      </List>
    </Drawer>
  );
};

export default Sidebar;
