// File: /src/components/ChatBox.jsx
import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import IconButton from "@mui/material/IconButton";
import SendIcon from "@mui/icons-material/Send";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";

const ChatBox = () => {
  const [messages, setMessages] = useState([
    { text: "Hi there! How can I assist you today?", sender: "bot" }
  ]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;

    const userMsg = { text: input, sender: "user" };
    const botReply = {
      text: "I'm still learning to respond. Stay tuned for full integration!",
      sender: "bot"
    };

    setMessages((prev) => [...prev, userMsg, botReply]);
    setInput("");
  };

  return (
    <Box sx={{ maxWidth: "600px", mx: "auto", mt: 4 }}>
      <Paper elevation={3} sx={{ p: 2, height: "400px", overflowY: "auto", mb: 2 }}>
        {messages.map((msg, i) => (
          <Typography
            key={i}
            align={msg.sender === "bot" ? "left" : "right"}
            sx={{
              mb: 1,
              p: 1,
              backgroundColor: msg.sender === "bot" ? "#f0f0f0" : "#d1e7dd",
              borderRadius: 2,
              maxWidth: "75%",
              ml: msg.sender === "bot" ? 0 : "auto"
            }}
          >
            {msg.text}
          </Typography>
        ))}
      </Paper>

      <Box sx={{ display: "flex", gap: 1 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Ask me something..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <IconButton color="primary" onClick={handleSend}>
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBox;
