import React from "react";
import { Box, Typography, Container, Chip, Stack } from "@mui/material";
import ChatBox from "../components/ChatBox";

const suggestions = [
  "What time is the Sunday service?",
  "What is your vision",
  "Do you have youth programs?",
  "How can I volunteer?",
];

const Home = () => {
  return (
    <Container maxWidth="md" sx={{ mt: 7, mb: 0, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Box textAlign="center" mb={4} width="100%">
        <Typography variant="h5" fontWeight="bold" gutterBottom>
         Welcome to the Chapel Digital Assistant
        </Typography>
        <Typography variant="body2" color="text.secondary">
          I'm here to answer questions about Nairobi Chapel Ngong' road and keep you updated.
        </Typography>
      </Box>

       <Box mb={3} textAlign="center" width="100%">
        <Typography variant="subtitle2" color="text.secondary" mb={1}>
          Try asking:
        </Typography>
        <Stack
          direction="row"
          spacing={1}
          justifyContent="center"
          flexWrap="wrap"
        >
          {suggestions.map((text) => (
            <Chip
              key={text}
              label={text}
              variant="outlined"
              color="primary"
              clickable
              sx={{
                borderRadius: "16px",
                fontWeight: 500,
                fontSize: "0.85rem",
              }}
              onClick={() => {
                const input = document.getElementById("assistant-input");
                if (input) input.value = text;
                input?.focus();
              }}
            />
          ))}
        </Stack>
      </Box>

      <Box width="100%" display="flex" justifyContent="center">
        <ChatBox />
      </Box>
    </Container>
  );
};

export default Home;
