import React from "react";
import {
  Box,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  Chip,
  Grid,
} from "@mui/material";

// Example announcements (static for now)
const announcements = [
  {
    title: "Upcoming Youth Worship Night",
    date: "July 28, 2025",
    description: "Join our teens and young adults for a powerful night of worship, fellowship, and fun.",
    image: "https://source.unsplash.com/800x400/?worship",
    tags: ["Youth", "Event"],
    link: "https://nairobichapel.org/youth-night",
  },
  {
    title: "New Sermon Series: Faith in Action",
    date: "Starts July 21, 2025",
    description: "Don't miss our next 4-week series exploring what real, practical faith looks like.",
    video: "https://www.youtube.com/embed/lTTajzrSkCw", // optional
    tags: ["Sermons", "Teaching"],
  },
  {
    title: "Tithe & Offering Info",
    date: "Ongoing",
    description: "You can now tithe via M-Pesa Paybill 123456 (Account: Tithe) or at give.nairobichapel.org",
    tags: ["Giving", "Finance"],
  },
];

const Announcements = () => {
  return (
    <Box p={3}>
      <Typography variant="h4" fontWeight={600} mb={1}>
        ðŸ“¢ Announcements
      </Typography>
      <Typography variant="body1" mb={4} color="text.secondary">
        Stay updated with what's happening at Nairobi Chapel Ngong Road.
      </Typography>

      {announcements.length === 0 ? (
        <Box textAlign="center" mt={5}>
          <Typography variant="h6" color="text.secondary">
            No announcements at the moment.
          </Typography>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {announcements.map((item, idx) => (
            <Grid item xs={12} md={6} lg={4} key={idx}>
              <Card elevation={3} sx={{ borderRadius: 3, overflow: "hidden" }}>
                {item.image && (
                  <CardMedia component="img" height="180" image={item.image} alt={item.title} />
                )}

                {item.video && (
                  <Box sx={{ position: "relative", pt: "56.25%" }}>
                    <iframe
                      src={item.video}
                      title={item.title}
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        border: 0,
                      }}
                    />
                  </Box>
                )}

                <CardContent>
                  <Typography variant="h6" fontWeight={600} gutterBottom>
                    {item.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" mb={1}>
                    {item.date}
                  </Typography>
                  <Typography variant="body2" color="text.primary">
                    {item.description}
                  </Typography>

                  <Box mt={2} display="flex" gap={1} flexWrap="wrap">
                    {item.tags &&
                      item.tags.map((tag, i) => (
                        <Chip
                          key={i}
                          label={tag}
                          size="small"
                          sx={{ backgroundColor: "#F3F4F6", fontSize: "0.75rem" }}
                        />
                      ))}
                  </Box>
                </CardContent>

                {item.link && (
                  <CardActions sx={{ px: 2, pb: 2 }}>
                    <Button
                      variant="outlined"
                      size="small"
                      href={item.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Learn More
                    </Button>
                  </CardActions>
                )}
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
};

export default Announcements;
