import React from "react";
import { Box, Typography, Chip, Stack } from "@mui/material";
import ChatBox from "../components/ChatBox";

const suggestions = [
  "What time is the Sunday service?",
  "Does the church have a vision?",
  "Do you have youth programs?",
  "How can I volunteer?",
];

const Home = () => {
  return (
    <Box sx={{
      width: '100%',
      height: '100vh',
      minHeight: '100vh',
      maxHeight: '100vh',
      overflow: 'hidden',
      overflowX: 'hidden',
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'flex-start',
    }}>
      <Box textAlign="center" mb={2} mt={8}>
        <Typography variant="h5" fontWeight="bold" gutterBottom>
         Welcome to the Chapel Digital Assistant
        </Typography>
        <Typography variant="body2" color="text.secondary">
          I'm here to answer questions about Nairobi Chapel Ngong' road and keep you updated on the latest.
        </Typography>
      </Box>

       <Box mb={2} textAlign="center">
        <Typography variant="subtitle2" color="text.secondary" mb={1}>
          Try asking:
        </Typography>
        <Stack
          direction="row"
          spacing={1}
          justifyContent="center"
          flexWrap="wrap"
        >
          {suggestions.map((text) => (
            <Chip
              key={text}
              label={text}
              variant="outlined"
              color="primary"
              clickable
              sx={{
                borderRadius: "16px",
                fontWeight: 500,
                fontSize: "0.85rem",
              }}
              onClick={() => {
                const input = document.getElementById("assistant-input");
                if (input) input.value = text;
                input?.focus();
              }}
            />
          ))}
        </Stack>
      </Box>

      <Box display="flex" justifyContent="center" width="100%" flexShrink={0}>
        <ChatBox />
      </Box>
    </Box>
  );
};

export default Home;
